# [doc = " The kind of error that occurred."] enum ErrorKind { # [doc = " An error that occurs when allocating a new state would result in an"] # [doc = " identifier that exceeds the capacity of a `StateID`."] StateIDOverflow { # [doc = " The maximum possible id."] max : u64 , # [doc = " The maximum ID requested."] requested_max : u64 , } , # [doc = " An error that occurs when adding a pattern to an Aho-Corasick"] # [doc = " automaton would result in an identifier that exceeds the capacity of a"] # [doc = " `PatternID`."] PatternIDOverflow { # [doc = " The maximum possible id."] max : u64 , # [doc = " The maximum ID requested."] requested_max : u64 , } , # [doc = " Occurs when a pattern string is given to the Aho-Corasick constructor"] # [doc = " that is too long."] PatternTooLong { # [doc = " The ID of the pattern that was too long."] pattern : PatternID , # [doc = " The length that was too long."] len : usize , } , }