# [doc = " A non-empty collection of non-empty patterns to search for."] # [doc = ""] # [doc = " This collection of patterns is what is passed around to both execute"] # [doc = " searches and to construct the searchers themselves. Namely, this permits"] # [doc = " searches to avoid copying all of the patterns, and allows us to keep only"] # [doc = " one copy throughout all packed searchers."] # [doc = ""] # [doc = " Note that this collection is not a set. The same pattern can appear more"] # [doc = " than once."] pub (crate) struct Patterns { # [doc = " The match semantics supported by this collection of patterns."] # [doc = ""] # [doc = " The match semantics determines the order of the iterator over patterns."] # [doc = " For leftmost-first, patterns are provided in the same order as were"] # [doc = " provided by the caller. For leftmost-longest, patterns are provided in"] # [doc = " descending order of length, with ties broken by the order in which they"] # [doc = " were provided by the caller."] kind : MatchKind , # [doc = " The collection of patterns, indexed by their identifier."] by_id : Vec < Vec < u8 > > , # [doc = " The order of patterns defined for iteration, given by pattern"] # [doc = " identifiers. The order of `by_id` and `order` is always the same for"] # [doc = " leftmost-first semantics, but may be different for leftmost-longest"] # [doc = " semantics."] order : Vec < PatternID > , # [doc = " The length of the smallest pattern, in bytes."] minimum_len : usize , # [doc = " The total number of pattern bytes across the entire collection. This"] # [doc = " is used for reporting total heap usage in constant time."] total_pattern_bytes : usize , }