# [doc = " A collection of sentinel state IDs for Aho-Corasick automata."] # [doc = ""] # [doc = " This specifically enables the technique by which we determine which states"] # [doc = " are dead, matches or start states. Namely, by arranging states in a"] # [doc = " particular order, we can determine the type of a state simply by looking at"] # [doc = " its ID."] pub (crate) struct Special { # [doc = " The maximum ID of all the \"special\" states. This corresponds either to"] # [doc = " start_anchored_id when a prefilter is active and max_match_id when a"] # [doc = " prefilter is not active. The idea here is that if there is no prefilter,"] # [doc = " then there is no point in treating start states as special."] pub (crate) max_special_id : StateID , # [doc = " The maximum ID of all the match states. Any state ID bigger than this"] # [doc = " is guaranteed to be a non-match ID."] # [doc = ""] # [doc = " It is possible and legal for max_match_id to be equal to"] # [doc = " start_anchored_id, which occurs precisely in the case where the empty"] # [doc = " string is a pattern that was added to the underlying automaton."] pub (crate) max_match_id : StateID , # [doc = " The state ID of the start state used for unanchored searches."] pub (crate) start_unanchored_id : StateID , # [doc = " The state ID of the start state used for anchored searches. This is"] # [doc = " always start_unanchored_id+1."] pub (crate) start_anchored_id : StateID , }