# [doc = " If code is not 0 (successful exit status), exit status is 101 (rust's default error code.)"] # [doc = " If `is_test` true and code is an error code, it will cause a panic."] pub fn detail_exit (code : i32 , is_test : bool) -> ! { if is_test { { :: core :: panicking :: panic_fmt (format_args ! ("status code: {0}" , code)) ; } ; } else { std :: process :: exit (code) ; } }