# [doc = " Generates a JSON report of all collected test cases."] pub fn generate_test_report_json (output_path : & Path , test_functions : Vec < TestInfo > ,) -> Result < () > { let json_content = serde_json :: to_string_pretty (& test_functions) . context ("Failed to serialize test info to JSON") ? ; fs :: write (output_path , json_content) . with_context (| | :: alloc :: __export :: must_use ({ :: alloc :: fmt :: format (format_args ! ("Failed to write aggregated test report to {0}" , output_path . display () ,) ,) })) ? ; { :: std :: io :: _print (format_args ! ("Aggregated test report generated at: {0}\n" , output_path . display () ,) ,) ; } ; Ok (()) }