# [doc = " The type of anchored search to perform."] # [doc = ""] # [doc = " If an Aho-Corasick searcher does not support the anchored mode selected,"] # [doc = " then the search will return an error or panic, depending on whether a"] # [doc = " fallible or an infallible routine was called."] # [non_exhaustive] pub enum Anchored { # [doc = " Run an unanchored search. This means a match may occur anywhere at or"] # [doc = " after the start position of the search up until the end position of the"] # [doc = " search."] No , # [doc = " Run an anchored search. This means that a match must begin at the start"] # [doc = " position of the search and end before the end position of the search."] Yes , }