# [doc = " A searcher that dispatches to one of several possible Teddy variants."] pub (crate) struct Searcher { # [doc = " The Teddy variant we use. We use dynamic dispatch under the theory that"] # [doc = " it results in better codegen then a enum, although this is a specious"] # [doc = " claim."] # [doc = ""] # [doc = " This `Searcher` is essentially a wrapper for a `SearcherT` trait"] # [doc = " object. We just make `memory_usage` and `minimum_len` available without"] # [doc = " going through dynamic dispatch."] imp : Arc < dyn SearcherT > , # [doc = " Total heap memory used by the Teddy variant."] memory_usage : usize , # [doc = " The minimum haystack length this searcher can handle. It is intended"] # [doc = " for callers to use some other search routine (such as Rabin-Karp) in"] # [doc = " cases where the haystack (or remainer of the haystack) is too short."] minimum_len : usize , }