use std::collections::HashSet;
use split_expanded_lib::{DeclarationItem};

# [doc = " A partitioning of bytes into equivalence classes."] # [doc = ""] # [doc = " A byte class set keeps track of an *approximation* of equivalence classes"] # [doc = " of bytes during NFA construction. That is, every byte in an equivalence"] # [doc = " class cannot discriminate between a match and a non-match."] # [doc = ""] # [doc = " Note that this may not compute the minimal set of equivalence classes."] # [doc = " Basically, any byte in a pattern given to the noncontiguous NFA builder"] # [doc = " will automatically be treated as its own equivalence class. All other"] # [doc = " bytes---any byte not in any pattern---will be treated as their own"] # [doc = " equivalence classes. In theory, all bytes not in any pattern should"] # [doc = " be part of a single equivalence class, but in practice, we only treat"] # [doc = " contiguous ranges of bytes as an equivalence class. So the number of"] # [doc = " classes computed may be bigger than necessary. This usually doesn't make"] # [doc = " much of a difference, and keeps the implementation simple."] pub (crate) struct ByteClassSet (ByteSet) ;