use std::collections::HashSet;
use split_expanded_lib::{DeclarationItem};

# [doc = " A candidate is the result of running a prefilter on a haystack at a"] # [doc = " particular position."] # [doc = ""] # [doc = " The result is either no match, a confirmed match or a possible match."] # [doc = ""] # [doc = " When no match is returned, the prefilter is guaranteeing that no possible"] # [doc = " match can be found in the haystack, and the caller may trust this. That is,"] # [doc = " all correct prefilters must never report false negatives."] # [doc = ""] # [doc = " In some cases, a prefilter can confirm a match very quickly, in which case,"] # [doc = " the caller may use this to stop what it's doing and report the match. In"] # [doc = " this case, prefilter implementations must never report a false positive."] # [doc = " In other cases, the prefilter can only report a potential match, in which"] # [doc = " case the callers must attempt to confirm the match. In this case, prefilter"] # [doc = " implementations are permitted to return false positives."] pub enum Candidate { # [doc = " No match was found. Since false negatives are not possible, this means"] # [doc = " the search can quit as it is guaranteed not to find another match."] None , # [doc = " A confirmed match was found. Callers do not need to confirm it."] Match (Match) , # [doc = " The start of a possible match was found. Callers must confirm it before"] # [doc = " reporting it as a match."] PossibleStartOfMatch (usize) , }