use std::collections::HashSet;
use split_expanded_lib::{DeclarationItem};

# [doc = " A builder for constructing a rare byte prefilter."] # [doc = ""] # [doc = " A rare byte prefilter attempts to pick out a small set of rare bytes that"] # [doc = " occurr in the patterns, and then quickly scan to matches of those rare"] # [doc = " bytes."] struct RareBytesBuilder { # [doc = " Whether this prefilter should account for ASCII case insensitivity or"] # [doc = " not."] ascii_case_insensitive : bool , # [doc = " A set of rare bytes, indexed by byte value."] rare_set : ByteSet , # [doc = " A set of byte offsets associated with bytes in a pattern. An entry"] # [doc = " corresponds to a particular bytes (its index) and is only non-zero if"] # [doc = " the byte occurred at an offset greater than 0 in at least one pattern."] # [doc = ""] # [doc = " If a byte's offset is not representable in 8 bits, then the rare bytes"] # [doc = " prefilter becomes inert."] byte_offsets : RareByteOffsets , # [doc = " Whether this is available as a prefilter or not. This can be set to"] # [doc = " false during construction if a condition is seen that invalidates the"] # [doc = " use of the rare-byte prefilter."] available : bool , # [doc = " The number of bytes set to an active value in `byte_offsets`."] count : usize , # [doc = " The sum of frequency ranks for the rare bytes detected. This is"] # [doc = " intended to give a heuristic notion of how rare the bytes are."] rank_sum : u16 , }