use std::collections::HashSet;
use split_expanded_lib::{DeclarationItem};

pub mod cli { use std :: path :: PathBuf ; use clap :: Parser ; use crate :: args :: Args ; use crate :: config_parser :: Config ; pub fn parse_arguments_and_config () -> anyhow :: Result < (Args , Option < Config >) > { let args = Args :: parse () ; let project_root = if args . path == PathBuf :: from (".") { std :: env :: current_dir () ? . parent () . unwrap () . to_path_buf () } else { args . path . clone () } ; let config = if let Some (config_file_path) = & args . config_file_path { Some (crate :: config_parser :: read_config (config_file_path , & project_root) ?) } else { let default_config_path = project_root . join ("config.toml") ; if default_config_path . exists () { Some (crate :: config_parser :: read_config (& default_config_path , & project_root ,) ? ,) } else { None } } ; Ok ((args , config)) } }