use std::collections::HashSet;
use split_expanded_lib::{DeclarationItem};

# [doc = " Write a prefix \"state\" indicator for fmt::Debug impls. It always writes"] # [doc = " exactly two printable bytes to the given formatter."] # [doc = ""] # [doc = " Specifically, this tries to succinctly distinguish the different types of"] # [doc = " states: dead states, start states and match states. It even accounts for"] # [doc = " the possible overlappings of different state types. (The only possible"] # [doc = " overlapping is that of match and start states.)"] pub (crate) fn fmt_state_indicator < A : Automaton > (f : & mut core :: fmt :: Formatter < '_ > , aut : A , id : StateID ,) -> core :: fmt :: Result { if aut . is_dead (id) { f . write_fmt (format_args ! ("D ")) ? ; } else if aut . is_match (id) { if aut . is_start (id) { f . write_fmt (format_args ! ("*>")) ? ; } else { f . write_fmt (format_args ! ("* ")) ? ; } } else if aut . is_start (id) { f . write_fmt (format_args ! (" >")) ? ; } else { f . write_fmt (format_args ! ("  ")) ? ; } Ok (()) }